body {
  font-family: 'Arial', sans-serif;
  margin: 20px;
  direction: rtl; /* RTL for Arabic layout */
}

.header {
  text-align: center;
  margin-bottom: 20px;
}

.company-info {
  font-weight: bold;
  font-size: 14px;
}

.contract-info {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
}

.contract-info td {
  padding: 8px;
  border: none;
}

.content {
  margin-bottom: 30px;
  line-height: 1.6;
  font-size: 14px;
}

.signatures {
  display: flex;
  justify-content: space-between;
  margin-top: 50px;
}

.signature-text {
  text-align: center;
  font-size: 14px;
  margin-bottom: 5px;
}

.signature-line {
  width: 200px;
  border-top: 1px solid black;
  margin: 0 auto;
}

.hr-details {
  margin-top: 20px;
  font-size: 14px;
}

